mapper.select:
select product_specification_id, product_specification, product_id, product_storage, product_price, status, product_specification_picture from product_specification where product_specification_id in
#{productSpecificationId, jdbcType=INTEGER}
order by field
#{productSpecificationId, jdbcType=INTEGER}

mapper.update:
update product_specification
set product_storage = (product_storage + #{number, jdbcType=INTEGER})
where product_specification_id = #{productSpecificationId, jdbcType=INTEGER}
